import axios from "axios";
import React, { useState, useRef } from "react";

export default function EditarInstitucion({ onInstitucionAdded }) {
  const modalRef = useRef(null);

  const [institucion, setInstitucion] = useState({
    idInstitucion: "",
    nombreInstitucion: "",
    direccionInstitucion: "",
    telefonoInstitucion: "",
    codigoPostal: "",
  });

  const {
    idInstitucion,
    nombreInstitucion,
    direccionInstitucion,
    telefonoInstitucion,
    codigoPostal,
  } = institucion;

  const onInputChange = (e) => {
    setInstitucion({ ...institucion, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    const urlBase = "http://localhost:8080/sipress-app/instituciones";
    await axios.post(urlBase, institucion);

    // Cerrar el modal manualmente
    const modalElement = modalRef.current;
    if (modalElement) {
      const modalInstance = new window.bootstrap.Modal(modalElement);
      modalInstance.hide();
    }

    // Llamar a la función de actualización de la lista
    onInstitucionAdded();
  };

  return (
    <div
      className="modal fade"
      id="EditarInstitucionModal"
      tabIndex="-1"
      aria-labelledby="EditarInstitucionModalLabel"
      aria-hidden="true"
      ref={modalRef}>
      <div className="modal-dialog modal-lg">
        <div className="modal-content bg-light">
          <div className="modal-header">
            <h1 className="modal-title fs-5" id="EditarInstitucionModalLabel">
              Registro de Instituciones
            </h1>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="modal"
              aria-label="Close">
              <span></span>
            </button>
          </div>
          <form onSubmit={onSubmit}>
            <div className="modal-body">
              <div className="form-floating form-group mb-3">
                <input
                  type="text"
                  className="form-control"
                  id="idInstitucion"
                  name="idInstitucion"
                  placeholder="ID Institución"
                  required={true}
                  value={idInstitucion}
                  onChange={onInputChange}
                />
                <label htmlFor="idInstitucion">ID de la Institución</label>
              </div>

              <div className="form-floating form-group mb-3">
                <input
                  type="text"
                  className="form-control"
                  id="nombreInstitucion"
                  name="nombreInstitucion"
                  placeholder="Nombre de la Institución"
                  required={true}
                  value={nombreInstitucion}
                  onChange={onInputChange}
                />
                <label htmlFor="nombreInstitucion">
                  Nombre de la Institución
                </label>
              </div>

              <div className="form-floating form-group mb-3">
                <input
                  type="text"
                  className="form-control"
                  id="direccionInstitucion"
                  name="direccionInstitucion"
                  placeholder="Dirección"
                  required={true}
                  value={direccionInstitucion}
                  onChange={onInputChange}
                />
                <label htmlFor="direccionInstitucion">
                  Dirección de la Institución
                </label>
              </div>

              <div className="form-floating form-group mb-3">
                <input
                  type="tel"
                  className="form-control"
                  id="telefonoInstitucion"
                  name="telefonoInstitucion"
                  placeholder="Teléfono de la Institución"
                  required={true}
                  value={telefonoInstitucion}
                  onChange={onInputChange}
                />
                <label htmlFor="telefonoInstitucion">
                  Teléfono de la Institución
                </label>
              </div>

              <div className="form-floating form-group mb-3">
                <input
                  type="text"
                  className="form-control"
                  id="codigoPostal"
                  name="codigoPostal"
                  placeholder="Código Postal"
                  required={true}
                  value={codigoPostal}
                  onChange={onInputChange}
                />
                <label htmlFor="codigoPostal">Código Postal</label>
              </div>
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary btn-sm me-3"
                data-bs-dismiss="modal">
                <i className="fa-regular fa-rectangle-xmark"></i> Cerrar
              </button>
              <button type="submit" className="btn btn-primary" data-bs-dismiss="modal">
                <i className="fa-solid fa-folder-plus"></i> Agregar registro
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
