import axios from "axios";
import React, { useEffect, useState } from "react";
import AgregarFormula from "../formularios/AgregarFormula";
import DetalleFormula from "./DetalleFormula";
import { Link } from "react-router-dom";
import { NumericFormat } from "react-number-format";

export default function ListadoFormulas() {
  const urlBase = "http://localhost:8080/sipress-app/formulas";
  const [formulas, setFormulas] = useState([]);
  const [selectedFormulaId, setSelectedFormulaId] = useState(null); // Estado para la fórmula seleccionada

  useEffect(() => {
    cargarFormulas();
  }, []);

  const cargarFormulas = async () => {
    const resultado = await axios.get(urlBase);
    setFormulas(resultado.data);
  };

  const eliminarFormula = async (id) => {
    const confirmacion = window.confirm(
      "¿Está seguro de que desea eliminar este registro?"
    );
    if (confirmacion) {
      await axios.delete(`${urlBase}/${id}`);
      cargarFormulas();
    }
  };

  return (
    <div className="p-3">
      <section>
        <AgregarFormula onFormulaAdded={cargarFormulas} />
        <div id="actions">
          <div className="row justify-content-center">
            <div className="col-12 col-md-4 d-flex justify-content-center">
              <a href="/" className="btn btn-info">
                <i className="fa-solid fa-arrow-left-long"></i> Ir a la página
                de inicio
              </a>
            </div>
            <div className="col-12 col-md-4 d-flex justify-content-center">
              <Link
                to="#"
                className="btn btn-success"
                data-bs-toggle="modal"
                data-bs-target="#AgregarFormulaModal">
                <i className="fa-regular fa-square-plus"></i> Agregar Registro
              </Link>
            </div>
          </div>
        </div>
      </section>
      <div className="row">
        <div className="col-md-9">
          <div className="card" id="contenedor-lista">
            <div className="card-header">
              <h3 className="text-center">
                <i class="fa-solid fa-pills"></i> Lista de Fórmulas Médicas
              </h3>
            </div>
            <div className="table-responsive">
              <table className="table table-striped table-hover align-middle">
                <thead className="table-dark">
                  <tr>
                    <th>Número de fórmula</th>
                    <th>Paciente</th>
                    <th>Medicación</th>
                    <th>Fecha de formulación</th>
                    <th>Valor de la medicación</th>
                    <th></th>
                  </tr>
                </thead>
                <tbody>
                  {
                    // Iterar sobre el arreglo de formulas
                    formulas.map((formula, indice) => (
                      <tr key={indice}>
                        <th scope="row">{formula.numeroFormula}</th>
                        <td>
                          {formula.paciente && (
                            <div>
                              <div>
                                {formula.paciente.nombrePaciente}{" "}
                                {formula.paciente.apellidoPaciente}
                              </div>
                              <div>ID: {formula.paciente.idPaciente}</div>
                            </div>
                          )}
                        </td>
                        <td>{formula.nombreMedicacion}</td>
                        <td>{formula.fechaMedicacion}</td>
                        <td>
                          <NumericFormat
                            value={formula.costoMedicacion}
                            displayType={"text"}
                            thousandSeparator=","
                            prefix={"$"}
                            decimalScale={2}
                            fixedDecimalScale
                          />
                        </td>
                        <td className="text-center">
                          <div>
                            <Link
                              to="#"
                              className="btn btn-info btn-sm me-2"
                              data-bs-toggle="modal"
                              data-bs-target="#DetalleFormulaModal"
                              onClick={() =>
                                setSelectedFormulaId(formula.idFormula)
                              }>
                              <i className="fa-regular fa-eye"></i> Detalles
                            </Link>
                            <Link
                              to={`/formulas/editar/${formula.idFormula}`}
                              className="btn btn-warning btn-sm me-2">
                              <i className="fa-regular fa-pen-to-square"></i>{" "}
                              Editar
                            </Link>
                            <button
                              onClick={() => eliminarFormula(formula.idFormula)}
                              className="btn btn-danger btn-sm">
                              <i className="fa-regular fa-trash-can"></i>{" "}
                              Eliminar
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))
                  }
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
      {selectedFormulaId && <DetalleFormula id={selectedFormulaId} />}
    </div>
  );
}
