import axios from "axios";
import React, { useEffect, useState, useCallback, useRef } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { NumericFormat } from "react-number-format";

export default function DetalleFormula() {
  const modalRef = useRef(null);
  const urlBase = "http://localhost:8080/sipress-app/formulas";

  let navigate = useNavigate();
  const { id } = useParams();

  const [formula, setFormula] = useState({
    numeroFormula: "",
    nombreMedicacion: "",
    fechaMedicacion: "",
    costoMedicacion: "",
    paciente: {
      idPaciente: "",
      nombrePaciente: "",
      apellidoPaciente: "",
      direccionPaciente: "",
      telefonoPaciente: "",
      emailPaciente: "",
      eps: {
        idEps: "",
        nombreEps: "",
        telefonoEps: "",
        emailEps: "",
      },
    },
  });

  const cargarFormula = useCallback(async () => {
    try {
      const resultado = await axios.get(`${urlBase}/${id}`);
      setFormula(resultado.data);
    } catch (error) {
      console.error("Error al cargar la fórmula:", error);
    }
  }, [id, urlBase]);

  useEffect(() => {
    cargarFormula();
  }, [cargarFormula]);

  const imprimirFormula = () => {
    // Lógica para imprimir el contenido del modal
    const modalContent = modalRef.current.innerHTML;
    const printWindow = window.open("", "", "height=600,width=800");
    printWindow.document.write(
      "<html><head><title>Detalles de la Fórmula</title>"
    );
    printWindow.document.write("</head><body >");
    printWindow.document.write(modalContent);
    printWindow.document.write("</body></html>");
    printWindow.document.close();
    printWindow.print();
  };

  return (
    <div
      className="modal fade"
      id="DetalleFormulaModal"
      tabIndex="-1"
      aria-labelledby="DetalleFormulaModalLabel"
      aria-hidden="true"
      ref={modalRef}>
      <div className="modal-dialog modal-lg">
        <div className="modal-content bg-light">
          <div className="modal-header">
            <h1 className="modal-title fs-5" id="DetalleFormulaModalLabel">
              Detalles de la Fórmula Médica
            </h1>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="modal"
              aria-label="Close">
              <span></span>
            </button>
          </div>
          <div className="modal-body">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">
                  Número de Fórmula: {formula.numeroFormula}
                </h5>
                <p className="card-text">
                  Medicamento: {formula.nombreMedicacion}
                </p>
                <p className="card-text">
                  Fecha de formulación: {formula.fechaMedicacion}
                </p>
                <p className="card-text">
                  Costo de la medicación:{" "}
                  <NumericFormat
                    value={formula.costoMedicacion}
                    displayType={"text"}
                    thousandSeparator={true}
                    prefix={"$"}
                  />
                </p>
                <h6>Detalles del Paciente</h6>
                <p className="card-text">
                  Nombre: {formula.paciente.nombrePaciente}{" "}
                  {formula.paciente.apellidoPaciente}
                </p>
                <p className="card-text">
                  Dirección: {formula.paciente.direccionPaciente}
                </p>
                <p className="card-text">
                  Teléfono: {formula.paciente.telefonoPaciente}
                </p>
                <p className="card-text">
                  Email: {formula.paciente.emailPaciente}
                </p>
                <h6>EPS</h6>
                <p className="card-text">
                  Nombre EPS: {formula.paciente.eps.nombreEps}
                </p>
                <p className="card-text">
                  Teléfono EPS: {formula.paciente.eps.telefonoEps}
                </p>
                <p className="card-text">
                  Email EPS: {formula.paciente.eps.emailEps}
                </p>
              </div>
            </div>
          </div>
          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-secondary btn-sm me-3"
              data-bs-dismiss="modal">
              <i className="fa-regular fa-rectangle-xmark"></i> Cerrar
            </button>
            <button
              type="button"
              className="btn btn-primary btn-sm"
              onClick={() => navigate(`/formulas/descargar/${id}`)}>
              
              <i className="fa-solid fa-print"></i> Imprimir
            </button>
            <button
              type="button"
              className="btn btn-success btn-sm"
              onClick={imprimirFormula}>
              <i className="fa-solid fa-download"></i> Descargar
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
